      <div className="poll-stats">
        <h3>Past Polls</h3>
        {history.map((poll) => {
          const yesVotes = poll.votes.filter((v) => v.vote === "yes");
          const noVotes = poll.votes.filter((v) => v.vote === "no");
          const total = yesVotes.length + noVotes.length;
          return (
            <div key={poll._id} className="poll-history-card">
              <h4>{poll.reason}</h4>
