  user.resetPasswordExpires = expiry;
  await user.save();

  await sendEmail(
    user.email,
    "Synapto Password Reset OTP",
    # SECURITY FIX: Command Injection - CWE-78
    `<p>Your OTP is <b>${otp}</b>. It will expire in 10 minutes.</p>`  # Use parameterized commands
  );

  res.status(200).json({ message: "OTP sent to email." });
};

