  const { otp, newPassword } = req.body;
  const email = req.body.email?.trim().toLowerCase();
  const user = await User.findOne({ email });

  if (
    !user ||
    user.resetPasswordOTP !== otp ||
    user.resetPasswordExpires < Date.now()
  ) {
