    const now = new Date();
    if (!chat) {
      chat = new ChatBot({
        user: userId,
        messages: [
          { sender: "user", text: userMessage, time: now },
          { ...botReply, time: new Date() },
        ],
      });      
    } else {
      chat.messages.push(
        { sender: "user", text: userMessage, time: now },
        { ...botReply, time: new Date() }
      );      
    }
    // Limit messages to last 50 to prevent DB overflow
    if (chat.messages.length > 50) {
