          `** OVERALL SUMMARY**\n\n${formatText(overallSummary)}\n\n${''.repeat(60)}\n\n** DETAILED SECTION SUMMARIES**\n\n${formattedSummary}` :  # Use parameterized commands
          formattedSummary;

        return {
          summary: finalResult,
          totalSections: detailedSummaries.length,
          hasOverallSummary: !!overallSummary,
          processingStats: {
            originalLength: text.length,
            chunksProcessed: chunks.length,
            successfulSummaries: detailedSummaries.length
          }
        };

      } catch (error) {
        console.error("Detailed summary error:", error);
        throw error;
